package testLeafJAVAProgramsForInterviews;

public class ConversionOfStringToInt {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			int number = 55;
			String valueOf = String.valueOf(number);
			System.out.println("Integer is converted to string :"+valueOf+50);
			String s = "30";
			int intValue = Integer.parseInt(s);
			int newly = intValue+20;
			System.out.println("String to integer :"+newly);

	}

}
