package week1homeworks;

public class LearningLogicalOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num0 = 5 ;
		int num2 = 6 ;
		boolean flag = true ;
		if(flag)
			System.out.println("the valueis false");
		if(num0 == 5 && num2++ == 6)
			System.out.println("the number is not same");
		if(num0 == 5||num2 == 6)
			System.out.println("the numbers are same");
		if(--num0 ==5 && num2++ == 7)
			System.out.println("your number");

	}

}
